package Fundamentos_operadores;

public class operadores_asignacion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    int numero = 10;
        //Forma abreviada
        numero += 5; //combinacion de operadores numero = numero + 5;
        numero -= 5;
        numero %=5;
        numero *=3;
        numero /=4;
	}

}
